import React, { useState, useEffect } from 'react';

function Cart() {
    const [cartData, setCartData] = useState([]); // To store cart data

    // Fetch cart data from db.json
    useEffect(() => {
        fetch("http://localhost:3000/cart") // API endpoint for cart details
            .then((response) => response.json())
            .then((data) => {
                setCartData(data); // Set fetched cart data
            })
            .catch((error) => console.error("Error fetching cart data:", error));
    }, []);

    return (
        <div className="container mt-4">
            <h1 className="text-center mb-4">Cart Transactions</h1>

            {cartData.length === 0 ? (
                <p>No transactions found.</p>
            ) : (
                <div>
                    {/* Render cart transaction details */}
                    <table className="table table-striped">
                        <thead>
                            <tr>
                                <th scope="col">ID</th>
                                <th scope="col">Product</th>
                                <th scope="col">Quantity</th>
                                <th scope="col">Total</th>
                                <th scope="col">Date</th>
                            </tr>
                        </thead>
                        <tbody>
                            {cartData.map((transaction, index) => (
                                <tr key={index}>
                                    <td>{transaction.id}</td>
                                    <td>
                                        <ul>
                                            {transaction.cartDetails.map((item, i) => (
                                                <li key={i}>{item.title} (x{item.quantity})</li>
                                            ))}
                                        </ul>
                                    </td>
                                    <td>{transaction.cartDetails.reduce((acc, item) => acc + item.quantity, 0)}</td>
                                    <td>${transaction.totalAmount.toFixed(2)}</td>
                                    <td>{new Date(transaction.createdAt).toLocaleDateString()}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
}

export default Cart;
