import React, { useState, useEffect } from 'react';
import { Link } from "react-router-dom";
import AddProduct from './Addproduct';
import ElectronicsSlider from './ElectronicsSlider';
import { FaShoppingCart } from 'react-icons/fa'; // Import the shopping cart icon from React Icons


function Home() {
    const [products, setProducts] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [cart, setCart] = useState([]); // Track items in the cart

    // Fetch products on load
    useEffect(() => {
        fetch("http://localhost:3000/products")
            .then((response) => {
                if (!response.ok) {
                    throw new Error("Network response was not ok");
                }
                return response.json();
            })
            .then((data) => {
                setProducts(data);
            })
            .catch((error) => console.error("Error fetching", error));
    }, []);

    // Load cart from localStorage if it exists
    useEffect(() => {
        const savedCart = JSON.parse(localStorage.getItem("cart"));
        if (savedCart) {
            setCart(savedCart);
        }
    }, []);

    // Save cart to localStorage whenever it changes
    useEffect(() => {
        localStorage.setItem("cart", JSON.stringify(cart));
    }, [cart]);

    // Filter products based on search term
    const filteredProducts = products.filter(product =>
        product.title.toLowerCase().includes(searchTerm.toLowerCase())
    );

    // Function to add product to cart
    const addToCart = (product) => {
        setCart((prevCart) => {
            const productIndex = prevCart.findIndex(item => item.id === product.id);

            if (productIndex !== -1) {
                // If product is already in the cart, increase quantity
                const updatedCart = [...prevCart];
                updatedCart[productIndex].quantity += 1;
                return updatedCart;
            } else {
                // If product is not in the cart, add it with quantity 1
                return [...prevCart, { ...product, quantity: 1 }];
            }
        });
    };

    // Function to increase quantity of a product
    const increaseQuantity = (productId) => {
        setCart((prevCart) =>
            prevCart.map(item =>
                item.id === productId ? { ...item, quantity: item.quantity + 1 } : item
            )
        );
    };

    // Function to decrease quantity of a product
    const decreaseQuantity = (productId) => {
        setCart((prevCart) =>
            prevCart.map(item =>
                item.id === productId && item.quantity > 1
                    ? { ...item, quantity: item.quantity - 1 }
                    : item
            )
        );
    };

    // Calculate total amount in the cart
    const totalAmount = cart.reduce((total, item) => total + item.price * item.quantity, 0);

    // Get the total number of items in the cart
    const totalItems = cart.reduce((total, item) => total + item.quantity, 0);

    // Function to close modal
    const closeModal = () => {
        const modal = new window.bootstrap.Modal(document.getElementById('cartModal'));
        modal.hide();
    };

    return (
        <>
            <ElectronicsSlider />
            <AddProduct />
            <div className="container mt-4">
                <h1 className="text-center mb-4">Products</h1>

                {/* Search Input */}
                <div className="mb-4 text-center">
                    <input
                        type="text"
                        placeholder="Search products..."
                        className="form-control"
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                    />
                </div>

                {/* Cart Info */}
                <div className="text-right mb-4">
                    <h5>Total Items in Cart: {totalItems}</h5>
                    {/* Cart Button that triggers the modal */}
                    <button
                        className="btn btn-primary"
                        data-bs-toggle="modal"
                        data-bs-target="#cartModal"
                    >
                        View Cart <FaShoppingCart />
                    </button>
                </div>

                <div className="row">
                    {Array.isArray(filteredProducts) && filteredProducts.length > 0 ? (
                        filteredProducts.map(product => (
                            <div className="col-md-4 mb-4" key={product.id}>
                                <div className="card h-100 product-card">
                                    <Link to={`/Productdetails/${product.id}`} >
                                        <img
                                            src={product.image}
                                            className="card-img-top"
                                            alt={product.title}
                                            style={{ height: '200px', objectFit: 'cover' }}
                                        />
                                        <div className="card-body">
                                            <h5 className="card-title">{product.title}</h5>
                                            <h5 className="card-title" style={{ color: 'red' }}>${product.price}</h5>
                                        </div>
                                    </Link>

                                    {/* Add to Cart Button with Icon, now outside the Link */}
                                    <button
                                        className="btn add-to-cart-btn"
                                        onClick={() => addToCart(product)}
                                    >
                                        <FaShoppingCart />
                                    </button>
                                </div>
                            </div>
                        ))
                    ) : (
                        <p className="text-center">No products available.</p>
                    )}
                </div>
            </div>

            {/* Modal to display cart items */}
            <div
                className="modal fade"
                id="cartModal"
                tabIndex="-1"
                aria-labelledby="cartModalLabel"
                aria-hidden="true"
            >
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="cartModalLabel">Shopping Cart</h5>
                            <button
                                type="button"
                                className="btn-close"
                                data-bs-dismiss="modal"
                                aria-label="Close"
                                onClick={closeModal}
                            ></button>
                        </div>
                        <div className="modal-body">
                            {cart.length === 0 ? (
                                <p>Your cart is empty.</p>
                            ) : (
                                <ul className="list-group">
                                    {cart.map((item, index) => (
                                        <li className="list-group-item" key={index}>
                                            <div className="d-flex justify-content-between">
                                                <span>{item.title}</span>
                                                <div>
                                                    <button
                                                        className="btn btn-sm btn-secondary"
                                                        onClick={() => decreaseQuantity(item.id)}
                                                    >
                                                        -
                                                    </button>
                                                    <span className="mx-2">{item.quantity}</span>
                                                    <button
                                                        className="btn btn-sm btn-secondary"
                                                        onClick={() => increaseQuantity(item.id)}
                                                    >
                                                        +
                                                    </button>
                                                </div>
                                                <span>${item.price * item.quantity}</span>
                                            </div>
                                        </li>
                                    ))}
                                </ul>
                            )}
                        </div>
                        <div className="modal-footer">
                            <button
                                type="button"
                                className="btn btn-secondary"
                                data-bs-dismiss="modal"
                                onClick={closeModal}
                            >
                                Close
                            </button>
                            <button
                                type="button"
                                className="btn btn-secondary"
                                onClick={closeModal}
                            >
                                Continue Shopping
                            </button>
                            <div>
                                <strong>Total: ${totalAmount.toFixed(2)}</strong>
                            </div>
                            {/* You can add a checkout button here */}
                            <button type="button" className="btn btn-primary">
                                Checkout
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default Home;
