import React, { useEffect, useState } from 'react';

function ProductScroll() {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        // Fetch products from the API
        fetch('/api/products') // Adjust endpoint as needed
            .then(response => response.json())
            .then(data => setProducts(data))
            .catch(error => console.error('Error fetching products:', error));
    }, []);

    return (
        <div className="product-scroll my-4">
            <div className="scroll-container d-flex overflow-auto">
                {products.map(product => (
                    <div key={product.id} className="product-card mx-2 p-2 border rounded bg-light">
                        <h6>{product.name}</h6>
                        <p>{product.description}</p>
                        <p>${product.price}</p>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default ProductScroll;
